interface ByMoralisProps {
  width?: number;
  variant?: "colour" | "dark" | "light";
  style?: React.CSSProperties;
}

export const ByMoralis = ({
  variant = "colour",
  width = 250,
  style,
}: ByMoralisProps) => {
  if (variant === "light") {
    return <PoweredByLight style={{ width, ...style }} />;
  }
  if (variant === "dark") {
    return <PoweredByDark style={{ width, ...style }} />;
  }
  return <PoweredByColour style={{ width, ...style }} />;
};
